import React, { useState } from "react";
import ReactDOM from "react-dom";
import moralis from "moralis";

moralis.initialize(process.env.REACT_APP_MORALIS_APPLICATION_ID);
moralis.serverURL = process.env.REACT_APP_MORALIS_SERVER_URL;

const initialUser = moralis.User.current();

const App = () => {
  const [user, setUser] = useState(initialUser);
  const [address, setAddress] = useState('');
  const onLogin = async () => {

        const user = await moralis.authenticate();
        setUser(user);
        setAddress(user.get("ethAddress"));
      };

      const onLogout = () => {
        moralis.User.logOut();
        setUser(null);
      };

      const saveTwitter = async () => {
        user.set( "twitter", document.getElementById('twitter').value);
        await user.save();
      };

      const saveInstagram = async () => {
        user.set( "instagram", document.getElementById('instagram').value);
        await user.save();
      };

  if (user) {
    return <div><button onClick={onLogout}>Logout {address}</button><br/><br/>Twitter : <input id="twitter" /> | <button onClick={saveTwitter}>Save</button><br/><br/>Instagram : <input id="instagram" /> | <button onClick={saveInstagram}>Save</button></div>;
  }
  return <button onClick={onLogin}>Login</button>;
};

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById("root")
);
