import React, { useState } from "react";
import ReactDOM from "react-dom";
import {DefaultButton, PrimaryButton, DangerButton, BrandButton} from 'pivotal-ui/react/buttons';
import {Grid, FlexCol} from 'pivotal-ui/react/flex-grids';
import {Panel} from 'pivotal-ui/react/panels';
import {Divider} from 'pivotal-ui/react/dividers';
import {Image} from 'pivotal-ui/react/images';
import {Input} from 'pivotal-ui/react/inputs';
import {Modal} from 'pivotal-ui/react/modal';

import logo from './logo.svg';
import './App.css';
import 'pivotal-ui/css/modal';
import moralis from "moralis";

moralis.initialize(process.env.REACT_APP_MORALIS_APPLICATION_ID);
moralis.serverURL = process.env.REACT_APP_MORALIS_SERVER_URL;

const orgaddresses = [];
function App() {

          const [clist, setList] = useState('');

        const Commons = moralis.Object.extend( "Commons", 
        {
          // Instance methods
             // hasSuperHumanStrength: function () {  return this.get("strength") > 18;  },
              // Instance properties go in an initialize method
             // initialize: function (attrs, options) { this.sound = "Rawr" }
        }, 
        {  
            newCommons: function() { 
                                        const commons = new Commons();
                                        commons.set( "name",        document.getElementById( 'commonsname' ).value  );
                                        commons.set( "vaultsymbol", document.getElementById( 'vaultsymbol' ).value  );
                                        commons.set( "organizer",   document.getElementById( 'organizer'   ).value  ); 
                                        commons.set( "vaultname",   document.getElementById( 'vaultname'   ).value  ); 
                                        return commons; 
                                    }
        });


         const saveCommons = async () => {    
                                              const newCommons = Commons.newCommons();
                                              await newCommons.save();  
                                         };


        const objects =[];
        let cList = <div></div>;
        const CommonsList = async () => 
        {
            const query = new moralis.Query( Commons );
            const results = await query.find();
            for ( let i = 0; i < results.length; i++ ) 
            { 
                const object = results[i];
                objects.push( object ); 
                const listItems = objects.map((commonsObject) =>  <li><a href={commonsObject.get('contract')} >{commonsObject.get('name')}</a></li> );
                setList(listItems);
            }
        }




        return (
                    <div className="App" >
                        <Panel {...{title: 'Overview', titleCols: [<FlexCol fixed><PrimaryButton  >Connect Wallet</PrimaryButton></FlexCol>], style: {  padding: '8px', background: '#f2f2f2'}}}>
                        <Grid className="grid-show show-outline">
                          <FlexCol fixed {...{style: {width: '35%'}}}/>
                          <FlexCol {...{style: {padding: '8px'}}} ><Input placeholder="Commons Name" id="commonsname" type="text"/></FlexCol>
                          <FlexCol className="col-grow-2"/>
                        </Grid>
                        <Grid className="grid-show show-outline">
                          <FlexCol fixed {...{style: {width: '35%'}}}/>
                          <FlexCol {...{style: {padding: '8px'}}} ><Input placeholder="Organizer" id="organizer" type="text"/></FlexCol>
                          <FlexCol className="col-grow-2 txt-l"><OrgModal /></FlexCol>
                        </Grid>
                        <Grid className="grid-show show-outline">
                          <FlexCol fixed {...{style: {width: '35%'}}}/>
                          <FlexCol {...{style: {padding: '8px'}}}><Input placeholder="Vault Coin Name" id="vaultname" type="text"/></FlexCol>
                          <FlexCol className="col-grow-2"/>
                        </Grid>
                        <Grid className="grid-show show-outline">
                          <FlexCol fixed {...{style: {width: '35%'}}}/>
                          <FlexCol {...{style: {padding: '8px'}}}><Input placeholder="Vault Coin Symbol" id="vaultsymbol" type="text"/></FlexCol>
                          <FlexCol className="col-grow-2"/>
                        </Grid>
                        <Grid className="grid-show show-outline">
                          <FlexCol fixed {...{style: {width: '35%'}}}/>
                          <FlexCol {...{style: {padding: '8px'}}} ><Input placeholder="Nbr Confrimations" id="confirmations" type="text"/></FlexCol>
                          <FlexCol className="col-grow-2"/>
                        </Grid>
                        <Grid className="grid-show show-outline">
                          <FlexCol fixed {...{style: {width: '35%'}}}/>
                          <FlexCol {...{style: {padding: '8px'}}} ><PrimaryButton>Deploy</PrimaryButton></FlexCol>
                          <FlexCol className="col-grow-2"/>
                        </Grid>



</Panel>
                    </div>
               );
}

class OrgModal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {show: false, disableAnimation: false};
  }

  render() {
    return (
      <div>
      <DefaultButton onClick={() => this.setState({show: true})}>
                      Open Modal
                    </DefaultButton>
        <Modal animationDuration={this.state.disableAnimation ? 0 : undefined}
                title="Add another Organizer!"
                size="30%"
                show={this.state.show}
                onHide={() => {this.setState({show: false})}}
                footer={<DefaultButton onClick={() => {this.setState({show: false})} }>Close</DefaultButton>}>
                <Input autoFocus placeholder="Organizer"/>
        </Modal>
      </div>
    );
  }
}

export default App;
