import React, { useState } from "react";
import ReactDOM from "react-dom";
import {DefaultButton, PrimaryButton, DangerButton, BrandButton} from 'pivotal-ui/react/buttons';
import {Grid, FlexCol} from 'pivotal-ui/react/flex-grids';
import {Panel} from 'pivotal-ui/react/panels';
import {Divider} from 'pivotal-ui/react/dividers';
import {Image} from 'pivotal-ui/react/images';
import {Input} from 'pivotal-ui/react/inputs';
import {Modal} from 'pivotal-ui/react/modal';
import {Checkbox} from 'pivotal-ui/react/checkbox';
import {Radio, RadioGroup} from 'pivotal-ui/react/radio';
import { ethers } from 'ethers'
import axios from 'axios';


import logo from './logo.svg';
import './App.css';
import 'pivotal-ui/css/modal';
import moralis from "moralis";

moralis.initialize(process.env.REACT_APP_MORALIS_APPLICATION_ID);
moralis.serverURL = process.env.REACT_APP_MORALIS_SERVER_URL;



const orgaddresses = [];
function App() {

        const [selectedFile, setFile] = useState('');
        const [selectedFileName, setFileName] = useState('');
        const [imageHash, setImageHash] = useState('');
        const [imageUrl, setImageUrl] = useState('profile.jpg');

        const initialUser = moralis.User.current();
        const [user, setUser] = useState(initialUser);
        const [clist, setList] = useState('');
        const [address, setAddress] = useState('Connect Wallet');
        const [chain, setChain] = useState('');
        const [connect, toggleConnect] = useState(false);
        const [twitter, setTwitter] = useState( 'Twitter' );
        const [instagram, setInstagram] = useState( 'Instagram' );

        const Avatar = moralis.Object.extend( "Avatar", 
        {
          // Instance methods
        }, 
        {  
            newAvatar: function() { 
                                        const avatar = new Avatar();
                     //                   commons.set( "name",        document.getElementById( 'commonsname' ).value  );
                       //                 commons.set( "vaultsymbol", document.getElementById( 'vaultsymbol' ).value  );
                         //               commons.set( "organizer",   document.getElementById( 'organizer'   ).value  ); 
                           //             commons.set( "vaultname",   document.getElementById( 'vaultname'   ).value  ); 
                                        return avatar; 
                                    }
        });
       const provider = new ethers.providers.Web3Provider(window.ethereum);

      window.addEventListener( 'load', async function() {    
                                                       if (user) 
                                                       {
                                                            await getNetwork();
                                                            setUser(user);
                                                            setAddress(user.get("ethAddress") ); 
                                                            if ( user.get("image") )
                                                            {
                                                                setImageUrl( 'https://gateway.pinata.cloud/ipfs/' + user.get("image") );
                                                            }
                                                       }

                                                  }) ;


// On file upload (click the upload button)
	const onFileUpload = async () => {

            const pinataApiKey = "769a94ec02018d9d3e92";
            const pinataSecretApiKey = "f1424ea5ce2fa199efa260ca2cae4c245227a750e919722ffdf5688b81638051";

            alert( process.env.REACT_APP_MORALIS_APPLICATION_ID );
           alert( process.env.REACT_APP_PINATA_PUBLIC );
            const url = `https://api.pinata.cloud/pinning/pinFileToIPFS`;
            let data = new FormData();
            data.append("file", selectedFile, selectedFile.name );
            const res = await axios.post(url, data, {
            maxContentLength: "Infinity", 
            headers: {
              "Content-Type": `multipart/form-data; boundary=${data._boundary}`,
              pinata_api_key: process.env.REACT_APP_PINATA_PUBLIC, 
              pinata_secret_api_key: process.env.REACT_APP_PINATA_PRIVATE,
            },
        });
            console.log(res.data);
      //      alert( res.data );
            setImageHash( res.data.IpfsHash );
            user.set( "image", res.data.IpfsHash );
            setImageUrl( 'https://gateway.pinata.cloud/ipfs/' + res.data.IpfsHash );
            await user.save();

           // this.setState({ imageMetaData : '{"name":"' + this.state.selectedFile.name + '","hash":"' + res.data.IpfsHash + '","description":"todo"}'});
        //{ "name":"My Kid's Art", "hash": "QmfAvnM89JrqvdhLymbU5sXoAukEJygSLk9cJMBPTyrmxo",  "by": "Justin Huner" }
	};

// On file select (from the pop up)
	const onFileChange = event => {
		// Update the state
	    setFile(event.target.files[0]);
	};

const getNetwork = () => {
    provider
      .getNetwork()
      .then((network) => {
        console.log('current chainId - ' + network.chainId)
        if (network.chainId === 100) {
          setChain('xDAI')
          toggleConnect(true)
        } else if (network.chainId === 4) {
          setChain('Rinkeby') 
          toggleConnect(true)
        } else if (network.chainId === 1) {
          setChain('Mainnet')
          toggleConnect(true)
        } else if (network.chainId === 3) {
          setChain('Ropsten')
          toggleConnect(true)
        } else if (network.chainId === 42) {
          setChain('Kovan')
          toggleConnect(true)
        } else {
          console.log('Pick a supported blockchain!')
        }
      })
      .catch((err) => {
        alert( 'err ' + err );
        console.log(err)
      })
  }

       const onLogin = async () => {                                        
                                        await getNetwork();
                                        const user = await moralis.authenticate();
                                        setUser(user);
                                        setAddress(user.get("ethAddress") );
                                    };

       const onLogout = () => {  moralis.User.logOut(); setUser(null); };


       return (
                    <div className="App" >
                        <Panel {...{title: 'Overview', titleCols: [<FlexCol fixed><PrimaryButton onClick={onLogin} >{address + ' - ' + chain}</PrimaryButton></FlexCol>], style: {  padding: '8px', background: '#f2f2f2'}}}>
                        <Grid className="grid-show show-outline">
                          <FlexCol fixed {...{style: {width: '35%'}}}/>
                          <FlexCol {...{style: {padding: '8px'}}} ><Input placeholder={twitter} id="twitter" type="text"/></FlexCol>
                          <FlexCol className="col-grow-2"/>
                        </Grid>
                       <Grid className="grid-show show-outline">
                          <FlexCol fixed {...{style: {width: '35%'}}}/>
                          <FlexCol {...{style: {padding: '8px'}}} ><Input placeholder={instagram} id="instagram" type="text"/></FlexCol>
                          <FlexCol className="col-grow-2"/>
                        </Grid>
                        <Grid className="grid-show show-outline">
                          <FlexCol fixed {...{style: {width: '35%'}}}/>
                          <FlexCol {...{style: {padding: '8px'}}} ><input type="file" onChange={onFileChange} />
				                                                    <button onClick={onFileUpload}>Upload!</button></FlexCol>
                          <FlexCol className="col-grow-2"><img className="avatarimage" src={imageUrl} /></FlexCol>
                        </Grid>
                      </Panel>
                    </div>
               );








}

export default App;
